const ActionCard = ({ action }: { action: Action }) => {
  const cardResource: { color: string; icon: React.ReactNode } =
    getResource(action);

  return (
    <Card
      component={ButtonBase}
      sx={{
        display: "flex",
        flexDirection: "column",
        borderRadius: "10px",
        justifyContent: "center",
        alignItems: "center",
        border: 1,
        boxShadow: 0,
        borderColor: cardResource.color,
        backgroundColor: "transparent",
        py: 4,
        position: "relative",
        width: { xs: "48%", md: "24%" },
        ":hover": {
          boxShadow: 5,
          "& .text": { display: "block" },
          "& .icon": { display: "none" },
        },
      }}
    >
      <Typography
        className="text"
        variant="h5"
        fontWeight={700}
        color={cardResource.color}
        display="none"
      >
        {capitalize(action)}
      </Typography>
      <SvgIcon
        className="icon"
        sx={{ fontSize: "70px", color: cardResource.color, display: "block" }}
      >
        {cardResource.icon}
      </SvgIcon>
    </Card>
  );
};

export default ActionCard;
